mapper.select:
SELECT CouName, record.WeekOfSemester AS WeekOfSemester, DayOfWeek, StartTime, EndTime, SignedTime, Status FROM tbl_course course LEFT JOIN tbl_teach teach ON course.CouID = teach.CouID LEFT JOIN tbl_schedule schedule ON teach.TeachID = schedule.TeachID LEFT JOIN tbl_record record ON record.SchID = schedule.SchID
record.SchId = #{schid}
AND record.StuID = #{stuid}
AND record.WeekOfSemester > #{startWeek} - 1
AND #{endWeek} + 1 > record.WeekOfSemester
AND record.semester = #{semester}

select
Status as status,
count(0) as number
from tbl_record
where StuID = #{stuid}
and Semester = #{semester}

    and SchID = #{schid}

group by Status

select

from tbl_record
where StuID = #{stuid,jdbcType=VARCHAR}
and SchID = #{schid,jdbcType=INTEGER}
and WeekOfSemester = #{weekofsemester,jdbcType=CHAR}
and Semester = #{semester,jdbcType=CHAR}

select s1.StuID
from (
  select StuID
  from tbl_select_course
  where TeachID in(
    select TeachID
    from tbl_schedule
    where SchID = #{schid,jdbcType=INTEGER}
  )
) s1
where s1.StuID not in (
  select StuID
  from tbl_record
  where SchID = #{schid,jdbcType=INTEGER}
  and WeekOfSemester = #{weekofsemester,jdbcType=CHAR}
)
